import React, { useState } from "react";

export const App = (props) => {
  const [state, setState] = useState(props);
  const [incompleteTodos, setIncompleteTodos] = useState([]);
  const [completeTodos, setCompleteTodos] = useState([]);
  const {title, deadline} = state;
  const onClickAdd = () => {
    if (title === '' || deadline === '') return
    const newState = setState[{...state, title: title, deadline: deadline}]
    setIncompleteTodos(newState);
    setState({})
  }

  return (
    <>
      <input value={title} onChange={e => setState({...state, title: e.target.value})} />
      <input value={deadline} onChange={e => setState({...state, deadline: e.target.value})} />
      <button onClick={onClickAdd}>TODO追加</button>
    </>
  );
}

App.defaultProps = {
  title: '',
  deadline: '',
}
